mapper.select:
SELECT A.*,B.D_DATA_ID,B.CLASS_NAME,B.META_DATA_NAME
FROM T_SOD_DATA_LOGIC A
LEFT JOIN T_SOD_DATA_CLASS B ON A.DATA_CLASS_ID = B.DATA_CLASS_ID WHERE A.DATABASE_ID = #{databaseId}

SELECT DISTINCT CASE TYPE WHEN 2 THEN DATA_CLASS_ID ELSE CONCAT(DATA_CLASS_ID, #{id}) END ID,CLASS_NAME NAME,PARENT_ID, CONCAT(CASE PARENT_ID WHEN '0' THEN '' ELSE PARENT_ID END, #{id}) PID,DATA_CLASS_ID,TYPE TYPE, CASE TYPE WHEN 1 THEN TRUE ELSE FALSE END isParent FROM T_SOD_DATA_CLASS WHERE DATA_CLASS_ID IN
#{id}
ORDER BY id

select a.*, b.c_datumtype as type_name,c.logic_name from t_sod_newdata_apply a
left join t_sod_data_datumtypeinfo b on a.d_data_id = b.c_datum_code
left join t_sod_logic_define c on a.logic_id = c.logic_flag


        a.examine_status = #{map.status}


        AND SUBSTR(b.C_DATUM_CODE,1,1) = #{map.dataType}

        select a.*,b.database_up_id,b.database_up_password,b.examine_status
        from t_sod_data_authority_apply a left join t_sod_database_user b on a.user_id = b.user_id and b.examine_status = 1
		where a.id = #{id}

select distinct d.*,dd.class_name,dd.is_access,dd.type_id,dd.type_name,dd.table_name,dd.special_database_name,dd.database_name from t_sod_data_authority_record d left join (select a.data_class_id,a.class_name,a.is_access,f.data_class_id type_id,f.class_name type_name,b.database_id,c.table_name,e.database_name special_database_name,ee.database_name from T_SOD_DATA_CLASS a left join T_SOD_DATA_CLASS f on SUBSTR(a.data_class_id,1,1) = f.data_class_id left join T_SOD_DATA_LOGIC b on a.data_class_id = b.data_class_id left join T_SOD_DATA_TABLE c on c.class_logic_id = b.id left join T_SOD_DATABASE e on b.database_id = e.id left join T_SOD_DATABASE_DEFINE ee on e.database_define_id = ee.id ) dd on (d.database_id = dd.database_id and d.data_class_id = dd.data_class_id)
d.apply_id = #{map.applyId}
AND type_name like '%${map.typeName}%'
AND class_name like '%${map.className}%'
AND table_name like '%${map.tableName}%'
AND database_name like '%${map.databaseName}%'
AND special_database_name like '%${map.specialDatabaseName}%'
AND apply_authority = #{map.applyAuthority}
AND authorize = #{map.authorize}

select d.*,dd.class_name,dd.is_access,dd.type_id,dd.type_name,dd.table_name,dd.special_database_name,dd.database_name from t_sod_data_authority_record d left join (select a.data_class_id,a.class_name,a.is_access,f.data_class_id type_id,f.class_name type_name,b.database_id,c.table_name,e.database_name special_database_name,ee.database_name from T_SOD_DATA_CLASS a left join T_SOD_DATA_CLASS f on SUBSTR(a.data_class_id,0,1) = f.data_class_id left join T_SOD_DATA_LOGIC b on a.data_class_id = b.data_class_id left join T_SOD_DATA_TABLE c on c.class_logic_id = b.id left join T_SOD_DATABASE e on b.database_id = e.id left join T_SOD_DATABASE_DEFINE ee on e.database_define_id = ee.id ) dd on (d.database_id = dd.database_id and d.data_class_id = dd.data_class_id)
d.apply_id = #{map.applyId}
AND type_name like '%${map.typeName}%'
AND class_name like '%${map.className}%'
AND table_name like '%${map.tableName}%'
AND database_name like '%${map.databaseName}%'
AND special_database_name like '%${map.specialDatabaseName}%'
AND apply_authority = #{map.applyAuthority}
AND authorize = #{map.authorize}

mapper.update:
update t_sod_data_authority_record


        authorize = #{authorize},


        cause = #{cause}


where  id = #{id}
